import React from 'react';
import Pokemon from './Pokemon';
import './pokedex.css';

class Pokedex extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      pokemonIndex: 0,
      currentIndex: 0,
    };
  }

  render() {
    const { pokemons } = this.props;
    return (
      <div className="pokedex">
        <Pokemon key={pokemons[currentIndex].id} pokemon={pokemons[currentIndex]} />
      </div>
    );
  }
}

export default Pokedex;
